sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	var state = g_form.getValue('u_state');
	if (state == '-5'||state =='-6'){
		var check = g_form.getValue('u_backup_type');
		if (check == 'snapshot'){
			var checkField = g_form.getValue('u_expiry7d');
			if (checkField !=''){
				var expiryDate = g_form.getValue("u_expiry7d");
				var todayDate = g_scratchpad.today;
				var format = g_user_date_format;

				var limitDate = todayDate +(7*1000*60*60*24);
				var expiryDateMs = getDateFromFormat(expiryDate, format);
				//g_form.addInfoMessage(expiryDateMs);
				//g_form.addInfoMessage(limitDate);

				if (expiryDateMs < todayDate){
					g_form.showFieldMsg('u_expiry_date', new GwtMessage().getMessage("{0} must be after Today", g_form.getLabelOf("u_expiry7d")));
					return false;
				}
				if (expiryDateMs === 0) {
					g_form.showFieldMsg(new GwtMessage().getMessage("{0} is invalid", g_form.getLabelOf("u_expiry7d")));
					return false;
				}
				if (limitDate < expiryDateMs) {
					g_form.showFieldMsg('u_expiry7d', new GwtMessage().getMessage("{0} must be within 7 days from now", g_form.getLabelOf("u_expiry7d")));
					return false;
				}
			}
		}
	}
}
