sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	var check = g_form.getValue('u_backup_type');
	if (check == 'snapshot'){
		var checkField = g_form.getValue('u_target_backup_snapshot');
		if (checkField !=''){

			var targetDate = g_form.getValue("u_target_backup_snapshot");
			var todayDate = g_scratchpad.today;
			var format = g_user_date_time_format;

			var targetDateMs = getDateFromFormat(targetDate, format);
			//g_form.addInfoMessage(targetDateMs);
			//g_form.addInfoMessage(todayDate);
			//g_form.addInfoMessage(todayTimeMs);

			if (targetDateMs > todayDate){
				//g_scratchpad.validate = 1;
				return true;
			}
			if (targetDateMs === 0) {
				g_form.addErrorMessage(new GwtMessage().getMessage("{0} is invalid", g_form.getLabelOf("u_target_backup_snapshot")));
				return false;
			}
			if (todayDate > targetDateMs) {
				g_form.showErrorBox('u_target_backup_snapshot', new GwtMessage().getMessage("{0} must be after Today", g_form.getLabelOf("u_target_backup_snapshot")));
				return false;
			}
		}
	}
}
