sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    var startDate = g_form.getValue("u_target_backup_snapshot");
    var endDate = g_form.getValue("u_expiry7d");
    var format1 = g_user_date_time_format;
	var format2 = g_user_date_format;

    if (startDate === "" || endDate === "")
        return true;

    // get date strings into a number of milliseconds since 1970-01-01
    var startDateMs = getDateFromFormat(startDate, format1);
	var endDateMs = getDateFromFormat(endDate, format2);

	if (startDateMs < endDateMs){
		g_form.hideFieldMsg('u_expiry7d', true);
		return true;
	}
	g_form.clearMessages();

	// 0 from "getDateFormat" means an invalid date string was passed to it
	//if (startDateMs === 0 || endDateMs === 0) {
        //if (startDate === 0)
            //g_form.showFieldMsg('u_schedule_start',new GwtMessage().getMessage("{0} is invalid", g_form.getLabelOf("u_schedule_start")));

        if (endDate === 0){
			g_form.hideFieldMsg('u_target_backup_snapshot', true);
            g_form.showFieldMsg('u_expiry7d',new GwtMessage().getMessage("{0} is invalid", g_form.getLabelOf("u_expiry7d")));

        return false;
    }

	if (startDateMs > endDateMs) {
		g_form.hideFieldMsg('u_target_backup_snapshot', true);
        g_form.showFieldMsg('u_expiry7d',new GwtMessage().getMessage("{0} must be after {1}", g_form.getLabelOf("u_expiry7d"), g_form.getLabelOf("u_target_backup_snapshot")));

        return false;

    }
}
